import React from 'react';

const CourseForm = (props)=>{
    return (
      <form onSubmit={props.addCourse}>
        <input type="text" onChange={props.addCourse}  />
        <button type="submit" onClick={() => this.addCourse(props.name)}>Add Course</button>
      </form>
    );
}

export default CourseForm;
