import React, {Component} from 'react';
import axios from "axios";
import Loader from "react-loader-spinner";
import CourseForm from "./components/CourseForm";
import CourseList from "./components/CourseList";

import "./App.css";




class App extends Component {
  state = {
    data: [],
    id: 0,
    name: null,
    loading: true,
    intervalIsSet: false,
    idToUpdate: null,
    nameToUpdate: null,
    current: ""
  };

  //! When a component mounts, first thing is to fetch all the existing data
  componentDidMount() {
    setTimeout(() => {
      this.setState({ loading: false });
    }, 1000);
    this.getDataFromDB();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDB, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  //! Get method to use our backend API and fetch data from DB
  getDataFromDB = () => {
    fetch("http://localhost:5000/getData")
      .then(data => data.json())
      .then(res => {
        this.setState({ data: res.data });
      })
      //* ALWAYS CATCH ERROR / EXCEPTIONS
      .catch(err => this.setState({ err }));
  };

  /*   // Update Course
  updateCourse =(e)=>{
    this.setState({
      current: e.target.value
    });
  } */

  // Add Course
  addCourse = name => {
    // post method that uses our API to create new data


    axios.post("http://localhost:5000/addData", {

      name: name
    });
  };

  /*   e.preventDefault();
    let current = this.state.current;
    let courses = this.state.courses;
    courses.push({ name: current });
    this.setState({
      courses,
      current: ""
    });
  }; */

  /*  // delete Course
  deleteCourse = (index) => {
    let courses = this.state.courses;
    courses.splice(index, 1);
    this.setState({
      courses
    })

  } */

  /*  //editCourse
  editCourse = (index,value)=> {
    let courses = this.state.courses;
    let course = courses[index];
    course['name'] = value;
    this.setState = ({
      courses
    })
  } */

  render() {
    let { data } = this.state;
    let length = data.length;

    const courseList =
      length > 0 ? (
        data.map(data => {
          return (
            <CourseList
              details={data.name}
              /* deleteCourse={this.deleteCourse} */
              courses={this.state.data}
              /* key={index}
            index={index}
            editCourse={this.editCourse} */
            />
          );
        })
      ) : (
        <p> Ther is no Course to show</p>
      );

    return (
      <div className="App">
        <h1>Add Course</h1>
        <CourseForm
          current={this.state.current}
          name= {this.state.name}
          setState= {e=> this.setState({name: e.target.value})}
          /* updateCourse={this.updateCourse} */ addCourse={this.addCourse}
        />

        <ul>{courseList}</ul>
      </div>
    );
  }
}


export default App;
