import React, {Component} from 'react';
import axios from "axios";
import Loader from "react-loader-spinner";
import CourseForm from "./components/CourseForm";
import CourseList from "./components/CourseList";

import "./App.css";




class App extends Component {
  state = {
    data: [],
    id: 0,
    name: null,
    idToDelete: null,
    loading: true,
    intervalIsSet: false,
    idToUpdate: null,
    nameToUpdate: null,
    current: ""
  };

  //! When a component mounts, first thing is to fetch all the existing data
  componentDidMount() {
    setTimeout(() => {
      this.setState({ loading: false });
    }, 1000);
    this.getDataFromDB();
    if (!this.state.intervalIsSet) {
      let interval = setInterval(this.getDataFromDB, 1000);
      this.setState({ intervalIsSet: interval });
    }
  }

  //! Get method to use our backend API and fetch data from DB
  getDataFromDB = () => {
    fetch("http://localhost:5000/getData")
      .then(data => data.json())
      .then(res => {
        this.setState({ data: res.data });
      })
      //* ALWAYS CATCH ERROR / EXCEPTIONS
      .catch(err => this.setState({ err }));
  };

  // Update Course
  updateCourse = e => {
    this.setState({
      current: e.target.value
    });
  };

  // Add Course
  addCourse = name => {
    // post method that uses our API to create new data
    let currentId = this.state.data.map(data => data.id);
    let idToBeAdded = 0;
    while (currentId.includes(idToBeAdded)) {
      ++idToBeAdded;
    }

    axios.post("http://localhost:5000/addData", {
      id: idToBeAdded,
      name: name
    });
  };



 /*  // delete Course
  deleteCourse = (index) => {
    let data = this.state.data;
    data.splice(index, 1);
    this.setState({
      data
    });
    // call /deleteData to delete the object using axios
    axios.delete("http://localhost:5000/deleteData", {
      data: data

    });
  } */

  /* //editCourse
  editCourse = (index,value)=> {
    let courses = this.state.courses;
    let course = courses[index];
    course['name'] = value;
    this.setState = ({
      courses
    })
  } */

  render() {
    let { data } = this.state;
    let length = data.length;

    const courseList =
      length <= 0 ? (
        <p> Ther is no Course to show</p>
      ) : (
        data.map(data => {
          return (
            <CourseList
              details={data.name}
              deleteCourse={this.deleteCourse}
              courses={this.state.data}
              editCourse={this.editCourse}
            />
          );
        })
      );

    return (
      <div className="App">
        <h1>Add Course</h1>
        <CourseForm
          current={this.state.current}
          name={this.state.name}
          ahmad={e => this.setState({ name: e.target.value })}
          /* updateCourse={this.updateCourse} */
          addCourse={this.addCourse}
        />
        <ul>{courseList}</ul>
      </div>
    );
  }
}


export default App;
