import React, { Component, Fragment } from 'react';
import "../App.css";
class CourseList extends Component {

    state ={
        isEdit : false
    }

    // render Update Form
  renderUpdateForm = ()=>{
    return (
      <form>
        <input type="text"/>
        <button>Update Course</button>
      </form>
    )
  }

  // toggleState
  toggleState = ()=>{
      let {isEdit} = this.state;
      this.setState({
          isEdit: !isEdit
      })
  }


    render() { 

        return(
            <ul>
             {
                 this.props.courses.map((course,index)=>{
                    return (
                      <Fragment key={index}>
                      {isEdit} ? {this.renderUpdateForm()} :
                        <li className="elements">
                          {course.name}
                          <button onClick = { ()=>{this.toggleState()}} className="btn">Edit Course</button>
                          <button className="btn" onClick={()=> {this.props.deleteCourse(index)}}>delete</button>
                        </li>
                      </Fragment>
                    );
                 })
             }

            </ul>
        )
    }
}

export default CourseList;
