import React, { Component, Fragment } from 'react';
import "../App.css";
class CourseList extends Component {

    render() { 

        return(
            <ul>
             {

                 this.props.courses.map((course)=>{
                    return (
                      <Fragment >
                        <li className="elements">
                          {course.name}
                                <button className="delete" onClick={this.props.deleteCourse(this.props.courses.id)}>delete</button>
                        </li>
                      </Fragment>
                    );
                 })
             }

            </ul>
        )
    }
}

export default CourseList;
